import React, { useState } from 'react';
import homeImage from "../../Media/introLeft.png";
import Button from "@material-ui/core/Button";
import { makeStyles } from '@material-ui/core/styles';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ImageIcon from '@material-ui/icons/Image';
import imageMenu from "../../Media/imageMenu.png";
import aboutImage from "../../Media/aboutImage.png";
import MissionModal from "./missionModal";
import GalleryModal from "./galleryModal";
import WhoModal from "./whoWeAreModal";
import { ButtonBase } from "@material-ui/core";
import {Dialog, DialogTitle, Divider, DialogContentText, DialogContent, CardHeader,
  Card, Typography, DialogActions, IconButton, CardContent, CardActions} from "@material-ui/core";
  import ImageGrid from "./ImageGrid";
  import TwitterIcon from '@material-ui/icons/Twitter';
  import FacebookIcon from '@material-ui/icons/Facebook';


const useStyles = makeStyles((theme) => ({
  
  }));


const MenuPropDisplay = (props) =>{
    const classes = useStyles();

    const [open, setOpen] = React.useState(false);

    
  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };




    let value = props.MenuArray;

    let [openProp, setProp] = useState(false);

  

    if(value === 0) return (
        <div>
          <Button onClick={()=> setProp(openProp = true)}>
            
        <img src={imageMenu}/>
        
        </Button>
         <Button
        variant="contained"
        color="secondary"

        onClick={handleClickOpen}
        style={{
          marginTop: '-20%'
        }}
      >
        View our work
      </Button>
      


      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >

        <DialogTitle id="alert-dialog-title">{"Responsive desgins , custom built for you"}</DialogTitle>
        <Divider variant="middle" />
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
            The Middle Space is commited to creating a responsive, quick and meaningful WebApp for your community.

          </DialogContentText>

          <Card className={classes.card} elevation={5} >
            <CardHeader

              title="Anthroposophical Movement"
              subheader="Beta - all rights reserved - 2020"
            />
            <br></br>
            <ImageGrid />
            <CardHeader

            />
            <CardContent>
              <Typography variant="body2" color="textSecondary" component="p">
                Having spent over a decade involved in both the Anthroposophic and Waldorf movement,
                I have a deep passsion for helping our communties find thier voice online.
        </Typography>
            </CardContent>
            <CardActions disableSpacing>
              <a href="https://twitter.com/TheMiddleSpace1" target="_blank" rel="noreferrer">
                <IconButton aria-label="delete">
                  <TwitterIcon />
                </IconButton>
              </a>
              <a href="https://twitter.com/TheMiddleSpace1" target="_blank" rel="noreferrer">
                <IconButton aria-label="delete">
                  <FacebookIcon />
                </IconButton>
              </a>

            </CardActions>

          </Card>

          <br></br>

          <Card className={classes.card} elevation={5}  >
            <CardHeader

              title="Local Businesses"
              subheader="Your personal touch online"
            />
            <br></br>
            <ImageGridLastStand />
            <CardHeader

            />
            <CardContent>

              <Typography variant="body2" color="textSecondary" component="p" >
                Helping you buisness find its path online, whether your an established company or a grass roots movement. Creating an online
             presence can help you find a larger voice.<br></br>
             Within just a few months , sites like these can greatly improve sales, brand connection and understanding of your mission.


        </Typography>
            </CardContent>
            <CardActions disableSpacing>
              <a href="https://twitter.com/TheMiddleSpace1" target="_blank" rel="noreferrer">
                <IconButton aria-label="delete">
                  <TwitterIcon />
                </IconButton>
              </a>
              <a href="https://twitter.com/TheMiddleSpace1" target="_blank" rel="noreferrer">
                <IconButton aria-label="delete">
                  <FacebookIcon />
                </IconButton>
              </a>

            </CardActions>

          </Card>


          <br></br>
        

        </DialogContent>
        <DialogActions>

          <Button onClick={handleClose} color="primary" autoFocus>
            Close
          </Button>
        </DialogActions>
      </Dialog>




















      </div>
    )
      




    else if (value === 2 ){
        return (
            <div>
              <Button>
            <img src={aboutImage}/>
            </Button>
           <WhoModal/>
          </div>
        )
    }




    else return (
        <div>
          <Button>
        <img src={homeImage}/>
        </Button>
      <MissionModal/>
      </div>
    )
}

export default MenuPropDisplay;
