import React, { useState } from 'react';
import './style.css';
import hoomeScreen from "../../Media/homeScreen.JPG";
import {useSpring, animated as a} from "react-spring";
import Paper from "@material-ui/core/Paper";
import Button from "@material-ui/core/Button";

const TestSpring = ()=>{
    const [flipped, set] = useState(false)
    const { transform, opacity } = useSpring({
      opacity: flipped ? 1 : 0,
      transform: `perspective(600px) rotateX(${flipped ? 180 : 0}deg)`,
      config: { mass: 5, tension: 500, friction: 80 }
    })
    return (
      <div onClick={() => set(state => !state)}>
        <a.div  style={{ opacity: opacity.interpolate(o => 1 - o), transform, }} ><img  style ={{height:'15%'}} src={hoomeScreen}/></a.div>
        <a.div  style={{ opacity, transform: transform.interpolate(t => `${t} rotateX(180deg)`) }}><img src={hoomeScreen}/></a.div>
      </div>
    )
  }


    

export default TestSpring;
