import React from 'react';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import ImageGrid from "./ImageGrid";
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import { red } from '@material-ui/core/colors';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import MissionBanner from "../../Media/aboutImage.png";
import TwitterIcon from '@material-ui/icons/Twitter';
import FacebookIcon from '@material-ui/icons/Facebook';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  root: {
    maxWidth: 345,
    margin:'auto'
  },
  media: {
    height: 0,
    paddingTop: '56.25%', // 16:9
  },
 
 
  expandOpen: {
    transform: 'rotate(180deg)',
  },
  avatar: {
    backgroundColor: red[500],
  },
}));




export default function GalleryModal() {
  const [open, setOpen] = React.useState(false);

  const classes = useStyles();
  const [expanded, setExpanded] = React.useState(false);



  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div>
    <Button
        variant="contained"
        color="secondary"
     
        onClick={handleClickOpen}
        style={{
            marginTop:'-20%'
        }}
      >
       View our work
      </Button>
      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">{"Use Google's location service?"}</DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
            Let Google help apps determine location. This means sending anonymous location data to
            Google, even when no apps are running.

          </DialogContentText>
          <Card className={classes.root}>
            <CardHeader

              title="Our Mission"
              subheader="finding your voice"
            />
            <CardMedia
              className={classes.media}
              image={MissionBanner}
              title="The Middle Space"
            />
            <CardContent>
              <Typography variant="body2" color="textSecondary" component="p">
              Web development isn't always easy , in fact it's often very confusing. 
              Our aim with “The Middle Space” is to create a way for every community to find their voice,
               regardless of size or budget. Having worked passionately within the special needs
                and Waldorf community for over a decade,
               we aim to inspire many more to find their digital voice.
        </Typography>
            </CardContent>
            <CardActions disableSpacing>
              <a href="https://twitter.com/TheMiddleSpace1" target="_blank" rel="noreferrer">
                <IconButton aria-label="delete">
                  <TwitterIcon />
                </IconButton>
              </a>
              <a href="https://twitter.com/TheMiddleSpace1" target="_blank" rel="noreferrer">
                <IconButton aria-label="delete">
                  <FacebookIcon />
                </IconButton>
              </a>
       
            </CardActions>
          
          </Card>
       
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Disagree
          </Button>
          <Button onClick={handleClose} color="primary" autoFocus>
            Agree
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
