import React from 'react';


import {useSpring, animated} from "react-spring";



const TestSpring = ()=>{

   


        const styleProp = useSpring({
            opacity: 1, from:{opacity:0.0}
        })
        

        return (

           

         
            <animated.h1 style={styleProp}> fading animation</animated.h1>


        )
            
        
    }


    

export default TestSpring;
